import React, { useEffect } from "react";

function Nav() {
  // Initialize the SideNav when the React Component mounts
  useEffect(() => {
    let sidenav = document.querySelector("#slide-out");
    // M.Sidenav.init(sidenav, {});
  }, []);

  return (
    <>
      <nav id="nav">
        {/* <div className="container"> */}
        <div className="nav-wrapper">
          <h1 className="nav-wrapper__logo">
            <a>Img</a>
          </h1>
          <ul className="nav-wrapper__menu">
            <li className="nav-wrapper__item">
              <a href="#about">About</a>
            </li>
            <li className="nav-wrapper__item">
              <a href="#projects">Work</a>
            </li>
            <li className="nav-wrapper__item">
              <a href="#contact">Contact</a>
            </li>
          </ul>

          <a className="nav-wrapper__toggle">
            <span></span>
          </a>
        </div>
        {/* </div> */}
      </nav>
    </>
  );
}

// Open and close mobile navigation
$('.nav-wrapper__toggle').click(function(event) {
  $('.nav').slideToggle(200);
  $('.navbar').toggleClass('open');
  $(this).toggleClass('open');
});

export default Nav;
